package cn.edu.fjnu.cdio.model.trs;

import java.io.Serializable;

/**
 * 
 * @author 曹欣炎
 *
 * 简介:
 *
 * 创建时间:
 * @version 2013-5-29 上午9:56:23
 */
public class CourseClassification implements Serializable {

	private static final long serialVersionUID = 1L;
	
	// 树节点id
	private Integer id;
	// 父节点id
	private Integer pId;
	// 名字
	private String name;

	public CourseClassification() {
		// TODO Auto-generated constructor stub
	}




	public CourseClassification(Integer id, Integer pId, String name) {
		this.id = id;
		this.pId = pId;
		this.name = name;
	}

	/**
	 * @return the id
	 */
	public Integer getId() {
		return id;
	}

	/**
	 * @param id
	 *            the id to set
	 */
	public void setId(Integer id) {
		this.id = id;
	}

	/**
	 * @return the pId
	 */
	public Integer getpId() {
		return pId;
	}

	/**
	 * @param pId
	 *            the pId to set
	 */
	public void setpId(Integer pId) {
		this.pId = pId;
	}

	/**
	 * @return the name
	 */
	public String getName() {
		return name;
	}

	/**
	 * @param name
	 *            the name to set
	 */
	public void setName(String name) {
		this.name = name;
	}

}
